function Add(){
	$("#tblData tbody").append(
		"<tr>"+
		"<td><input type='text'/></td>"+
		"<td><input type='text'/></td>"+
		"<td><input type='text'/></td>"+
		"<td><img src='images/disk.png' class='btnSave'><img src='images/delete.png' class='btnDelete'/></td>"+
		"</tr>");
	
		$(".btnSave").bind("click", Save);		
		$(".btnDelete").bind("click", Delete);
}; 


function Save(){
	var par = $(this).parent().parent(); //tr
	var tdStock = par.children("td:nth-child(1)");
	var tdAmount = par.children("td:nth-child(2)");
	var tdTime = par.children("td:nth-child(3)");
	var tdButtons = par.children("td:nth-child(4)");

	tdName.html(tdStock.children("input[type=text]").val());
	tdPhone.html(tdAmount.children("input[type=text]").val());
	tdEmail.html(tdTime.children("input[type=text]").val());
	tdButtons.html("<img src='images/delete.png' class='btnDelete'/><img src='images/pencil.png' class='btnEdit'/>");

	$(".btnEdit").bind("click", Edit);
	$(".btnDelete").bind("click", Delete);
}; 

function Edit(){
	var par = $(this).parent().parent(); //tr
	var tdStock = par.children("td:nth-child(1)");
	var tdAmount = par.children("td:nth-child(2)");
	var tdTime = par.children("td:nth-child(3)");
	var tdButtons = par.children("td:nth-child(4)");

	tdStock.html("<input type='text' id='txtName' value='"+tdStock.html()+"'/>");
	tdAmount.html("<input type='text' id='txtPhone' value='"+tdAmount.html()+"'/>");
	tdTime.html("<input type='text' id='txtEmail' value='"+tdTime.html()+"'/>");
	tdButtons.html("<img src='images/disk.png' class='btnSave'/>");

	$(".btnSave").bind("click", Save);
	$(".btnEdit").bind("click", Edit);
	$(".btnDelete").bind("click", Delete);
};


function Delete(){
	var par = $(this).parent().parent(); 
	par.remove();
}; 




